import { Container, Row, Col } from 'react-bootstrap';
import { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state={
      text:'',
      result:''
    }
    this.add = this.add.bind(this)
  }
  add =(e)=>{
    this.setState({
      text:this.state.text+e.currentTarget.value,
      result:''
    })
  }
  render() {
    return (
      <Container>
        <Row>
          <Col xs={4}></Col>
          <Col xs={4} style={{ border: "1px solid black" }}>
            <Row>
              <input type="text" className="calculator-screen z-depth-1" value={this.state.text} disabled />
              <div className="Keys">
                <button type="button" className="operator btn btn-info" value="+" onClick={this.add}>+</button>
                <button type="button" className="operator btn btn-info" value="-" onClick={this.add}>-</button>
                <button type="button" className="operator btn btn-info" value="*" onClick={this.add}>&times;</button>
                <button type="button" className="operator btn btn-info" value="/" onClick={this.add}>&divide;</button>

                <button type="button" value="7" className="btn btn-light waves-effect" onClick={this.add}>7</button>
                <button type="button" value="8" className="btn btn-light waves-effect" onClick={this.add}>8</button>
                <button type="button" value="9" className="btn btn-light waves-effect">9</button>

                <button type="button" className="operator btn btn-info" value="/" onClick={this.add}>&divide;</button>

                <button type="button" value="4" className="btn btn-light waves-effect">4</button>
                <button type="button" value="5" className="btn btn-light waves-effect">5</button>
                <button type="button" value="6" className="btn btn-light waves-effect">6</button>

                <button type="button" value="1" className="btn btn-light waves-effect">1</button>
                <button type="button" value="2" className="btn btn-light waves-effect">2</button>
                <button type="button" value="3" className="btn btn-light waves-effect">3</button>

                <button type="button" value="0" className="btn btn-light waves-effect">0</button>
                <button type="button" className="decimal function btn btn-secondary" value=".">.</button>
                <button type="button" className="all-clear function btn btn-danger btn-sm" value="all-clear">AC</button>

                <button type="button" className="equal-sign operator btn btn-primary" value="=">=</button>
              </div>
            </Row>
          </Col>
          <Col xs={4}></Col>
        </Row>
      </Container>
    );
  }
}

export default App;
